#include <stdio.h>
#include <stdlib.h>


struct TreeNode* buildTreeHelper(int* preorder, int preStart, int preEnd, int* inorder, int inStart, int inEnd, int* inorderMap) {
    if (preStart > preEnd || inStart > inEnd) {
        return NULL;
    }

    int rootVal = preorder[preStart];
    struct TreeNode* root = (struct TreeNode*)malloc(sizeof(struct TreeNode));
    root->val = rootVal;

    int inRootIndex = inorderMap[rootVal + 3000]; // Adjusting for negative values

    int leftSubtreeSize = inRootIndex - inStart;

    root->left = buildTreeHelper(preorder, preStart + 1, preStart + leftSubtreeSize, inorder, inStart, inRootIndex - 1, inorderMap);
    root->right = buildTreeHelper(preorder, preStart + leftSubtreeSize + 1, preEnd, inorder, inRootIndex + 1, inEnd, inorderMap);

    return root;
}

struct TreeNode* buildTree(int* preorder, int preorderSize, int* inorder, int inorderSize) {
    if (preorderSize == 0 || inorderSize == 0) {
        return NULL;
    }

    // Create a map to store the indices of elements in the inorder array for O(1) access
    int* inorderMap = (int*)malloc(6001 * sizeof(int)); // Considering values from -3000 to 3000
    for (int i = 0; i < inorderSize; i++) {
        inorderMap[inorder[i] + 3000] = i; // Adjusting index to handle negative values
    }

    struct TreeNode* root = buildTreeHelper(preorder, 0, preorderSize - 1, inorder, 0, inorderSize - 1, inorderMap);
    free(inorderMap);
    return root;
}
