// Comparison function for qsort (descending order)
int compare(const void *a, const void *b) {
    return (*(int*)b - *(int*)a);
}

int hIndex(int* citations, int citationsSize) {
    // Sort citations in descending order
    qsort(citations, citationsSize, sizeof(int), compare);

    int h = 0;

    // Check each possible h-index value
    for (int i = 0; i < citationsSize; i++) {
        // At position i, we have (i+1) papers
        // citations[i] is the citation count of the (i+1)th most cited paper

        // For h-index to be (i+1), we need:
        // 1. At least (i+1) papers with >= (i+1) citations
        // 2. citations[i] >= (i+1) (the (i+1)th paper has at least (i+1) citations)

        if (citations[i] >= i + 1) {
            h = i + 1;  // We can achieve h-index of (i+1)
        } else {
            break;  // No point checking further as array is sorted
        }
    }

    return h;
}
